const raycaster = new THREE.Raycaster();
const mouse = new THREE.Vector2();

function init(){
	onWindowResize();
}

function onWindowResize() {

	camera.aspect = window.innerWidth / window.innerHeight;
	camera.updateProjectionMatrix();

	renderer.setSize( window.innerWidth, window.innerHeight );


	//if(deviceInfo == 0)
	//  	renderer.setSize( window.innerWidth, window.innerHeight );
  	//else
  	//  	renderer.setSize( preMetaWidth, preMetaHeight );
}

function pointerdown ( event ) {
	onWindowResize();
}

function pointerup ( event ) { 
	// update the picking ray with the camera and mouse position
	raycaster.setFromCamera( mouse, camera );

	// calculate objects intersecting the picking ray
	const intersects = raycaster.intersectObjects( scene.children, true );

	for(var i = 0; i < intersects.length; i++){
		if(intersects[i].object.name == this.name){
			window.open("https://liminalvr.com");
		}
	}

	if(intersects.length > 0){
		var firstIntersect = intersects[0];
		console.log(firstIntersect.object.name);
	}	
	//nsole.log(firstIntersect);
	//window.open("https://www.w3schools.com");
}

function pointermove( event ) {
	// calculate mouse position in normalized device coordinates
	// (-1 to +1) for both components

	mouse.x = ( event.clientX / window.innerWidth ) * 2 - 1;
	mouse.y = - ( event.clientY / window.innerHeight ) * 2 + 1;
}


